mapper.select:
 		UPDATE write_KnowBoard
			SET STATE = 'N'
		WHERE SEQ = #{seq}

UPDATE write_KnowBoard


	SET R_SEQ = 1, TITLE = #{title} , CONTENT = #{content}, SUBJECT = #{subject}


	SET R_SEQ = R_SEQ + 1, TITLE = #{title} , CONTENT = #{content}, SUBJECT = #{subject}


WHERE seq = #{seq}

UPDATE write_KnowBoard
SET HITS = HITS +1
WHERE seq = #{seq}
